
public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// looping means to continue
		// purpose of this program is to continue printing all the value i begining from 0 and end 19
		
		for(int i=0;  // started the value i = 0
				i<20;
				i+=1)  // or i++
		{
			System.out.println(i);			
			
		}
		

	}

}
