
public class Inheritance extends non_static {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	   // Inheritance helps us to make functions or variable from a different class  
      //To create object for an intance use the class name.
		
		Inheritance x = new Inheritance(); // instance of a class
		x.car();
		x.bible();
		x.flower();
		country(); // this is static function
		
	}
	
	public static void country()
	
	{
		System.out.println("I am from Ethiopia");
	}

}
