
public class Single_array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Rainbow [] = new String[7];
		// []  row numbers
		Rainbow [0]= "red";
		Rainbow [1]= "yellow";
		Rainbow [2]= "blue";
		Rainbow [3]= "Green";
		Rainbow [4]= "violet";
		Rainbow [5]= "indigo";
		Rainbow [6]= "orange";
		
		//System.out.println(Rainbow[0]);
		
		//System.out.println(Rainbow[4]);
		
		//System.out.println(Rainbow[5]);
		
		// length is table row count
		
		//System.out.println(Rainbow.length);
		
		// To print out the values of each arrays use -for loop
		
		 	 for(int x=0; x<Rainbow.length; x++)
	
		{
			System.out.println(Rainbow[x]);
		}
		

	}

}
